mapper.select:
SELECT seq, id, name, last_login_dt
  from herit_member
 where
       id   = #{id}
   and pass = #{pass}

mapper.update:
UPDATE herit_member
   set last_login_dt = current_timestamp
 WHERE seq = #{seq}

mapper.select:
SELECT a.seq, a.id, a.name, a.phone, a.birthday, a.sex, a.agree_dt, a.mark_chk, a.mark_dt, a.mem_sts, a.use_yn, date_format(a.reg_dt, '%Y-%m-%d %H:%i') as reg_dt, date_format(a.last_login_dt, '%Y-%m-%d %H:%i') as last_login_dt, date_format(a.st_dt, '%Y-%m-%d %H:%i') as st_dt FROM herit_member a WHERE 1=1
and a.mem_sts = ${mem_sts}
and id like CONCAT('%',#{searchString},'%')
and name like CONCAT('%',#{searchString},'%')
and ( phone like CONCAT('%',#{searchString},'%') )
order by reg_dt desc LIMIT #{perPageCnt} OFFSET #{pageStartNo}

mapper.update:
update herit_member
   set pass = ${pass}
       sts_dt = current_timestamp
 where seq = #{seq}
