mapper.select:
SELECT count(*) FROM ( SELECT a.seq, a.id, a.phone, a.name, sum(case when b.use_yn = 'Y' then b.point else 0 end) as plus_point, sum(case when b.use_yn = 'P' then b.point else 0 end) as minus_point, sum(case when b.use_yn = 'N' then b.point else 0 end) as del_point, sum(case when b.use_yn = 'Y' then b.point else 0 end) - sum(case when b.use_yn = 'P' then b.point else 0 end) - sum(case when b.use_yn = 'N' then b.point else 0 end) as total_point FROM herit_member a left outer join herit_mem_point b on a.seq = b.mem_seq WHERE 1=1
and a.id like CONCAT('%',#{searchString},'%')
and a.name like CONCAT('%',#{searchString},'%')
and a.phone like CONCAT('%',#{searchString},'%')
and date_format(a.reg_dt, '%Y-%m-%d') between #{searchFromDt} and #{searchToDt}
and a.use_yn = #{pay_sts} and date_format(a.reg_dt, '%Y-%m-%d') between #{searchFromDt} and #{searchToDt}
group by a.seq, a.id, a.phone, a.name order by a.seq desc ) AS T WHERE 1 = 1
and total_point between #{searchFromPoint} and #{searchToPoint}

SELECT seq, id, phone, name, format(plus_point, 0) as plus_point, format(minus_point, 0) as minus_point, format(del_point, 0) as del_point, format(total_point, 0) as total_point FROM ( SELECT a.seq, a.id, a.phone, a.name, sum(case when b.use_yn = 'Y' then b.point else 0 end) as plus_point, sum(case when b.use_yn = 'P' then b.point else 0 end) as minus_point, sum(case when b.use_yn = 'N' then b.point else 0 end) as del_point, sum(case when b.use_yn = 'Y' then b.point else 0 end) - sum(case when b.use_yn = 'P' then b.point else 0 end) - sum(case when b.use_yn = 'N' then b.point else 0 end) as total_point FROM herit_member a left outer join herit_mem_point b on a.seq = b.mem_seq WHERE 1=1
and a.id like CONCAT('%',#{searchString},'%')
and a.name like CONCAT('%',#{searchString},'%')
and a.phone like CONCAT('%',#{searchString},'%')
and date_format(a.reg_dt, '%Y-%m-%d') between #{searchFromDt} and #{searchToDt}
and a.use_yn = #{pay_sts} and date_format(a.reg_dt, '%Y-%m-%d') between #{searchFromDt} and #{searchToDt}
group by a.seq, a.id, a.phone, a.name order by a.seq desc ) AS T WHERE 1 = 1
and total_point between #{searchFromPoint} and #{searchToPoint}
LIMIT #{perPageCnt} OFFSET #{pageStartNo}

SELECT a.seq, a.id, a.phone, a.name, format(sum(case when b.use_yn = 'Y' then b.point else 0 end), 0) as plus_point, format(sum(case when b.use_yn = 'P' then b.point else 0 end), 0) as minus_point, format(sum(case when b.use_yn = 'N' then b.point else 0 end), 0) as del_point, format(sum(case when b.use_yn = 'Y' then b.point else 0 end) - sum(case when b.use_yn = 'P' then b.point else 0 end) - sum(case when b.use_yn = 'N' then b.point else 0 end), 0) as total_point FROM herit_member a left outer join herit_mem_point b on a.seq = b.mem_seq WHERE 1 = 1
and a.seq = #{seq}
and a.seq = #{mem_seq}
group by a.seq, a.id, a.phone, a.name


 	SELECT 
 			count(*)                   			
  	 FROM herit_member a left outer join herit_mem_point b on a.seq = b.mem_seq
  		WHERE  1 = 1 
            

		  and a.seq = #{seq}
  

		  and a.seq = #{mem_seq}

SELECT a.seq, a.id, a.phone, a.name, case when b.use_yn = 'Y' then format(b.point, 0) else 0 end as plus_point, case when b.use_yn = 'P' then format(b.point, 0) else 0 end as minus_point, case when b.use_yn = 'N' then format(b.point, 0) else 0 end as del_point, format(b.rest_point, 0) as total_point, b.content, date_format(b.point, 0) as str_point, b.use_yn, date_format(b.reg_dt, '%Y-%m-%d %H:%i') as reg_dt FROM herit_member a left outer join herit_mem_point b on a.seq = b.mem_seq WHERE 1 = 1
and a.seq = #{seq}
and a.seq = #{mem_seq}
order by a.seq desc LIMIT #{perPageCnt} OFFSET #{pageStartNo}
