mapper.select:
	    select count(*) as cnt
	      from (
        
            SELECT                  
                  date_format(a.pay_dt, '%Y-%m-%d') as pay_dt, 
                  '포인트 몰' as pay_type_name, 
                  a.send_sts, 
                  case when a.send_sts = 'Y' then '발송완료'
                       when a.send_sts = 'N' then '미발송'
                       else ''
                       end as send_sts_name, 
                  count(a.seq) as pay_count
             FROM herit_pay_info a
            WHERE  pay_type = #{pay_type}
        
        
        	  and a.mem_seq = #{mem_seq}        
        
        
        	
        		and date_format(pay_dt, '%Y.%m.%d') between #{searchFromDt} and #{searchToDt}
        	
        	
        		and pay_sts = #{pay_sts} and date_format(pay_dt, '%Y.%m.%d') between #{searchFromDt} and #{searchToDt}
        	       	        	
        
        group by  date_format(a.pay_dt, '%Y-%m-%d')
        ) as T

SELECT date_format(a.pay_dt, '%Y-%m-%d') as pay_dt, '포인트 몰' as pay_type_name, a.send_sts, case when a.send_sts = 'Y' then '발송완료' when a.send_sts = 'N' then '미발송' else '' end as send_sts_name, count(a.seq) as pay_count FROM herit_pay_info a WHERE a.pay_type = #{pay_type}
and a.mem_seq = #{mem_seq}
and date_format(pay_dt, '%Y.%m.%d') between #{searchFromDt} and #{searchToDt}
and pay_sts = #{pay_sts} and date_format(pay_dt, '%Y.%m.%d') between #{searchFromDt} and #{searchToDt}
group by date_format(a.pay_dt, '%Y-%m-%d') order by pay_dt desc LIMIT #{perPageCnt} OFFSET #{pageStartNo}

mapper.update:
	UPDATE herit_pay_info 
	   SET 
	       send_sts = #{send_sts}
     WHERE
           date_format(pay_dt, '%Y-%m-%d') = #{pay_dt}

mapper.select:

    SELECT                  
          count(*)
     FROM herit_pay_info a, herit_member b, herit_payplan_info c
    WHERE  a.pay_type = #{pay_type}
      and  a.mem_seq = b.seq
      and  a.p_key   = c.seq


	  and c.seq = #{seq}


	  and a.mem_seq = #{mem_seq}        
        

	  and date_format(a.pay_dt, '%Y.%m.%d') = #{searchDt}


	
		and date_format(a.pay_dt, '%Y.%m.%d') between #{searchFromDt} and #{searchToDt}
	
	
		and a.pay_sts = #{pay_sts} and date_format(a.pay_dt, '%Y.%m.%d') between #{searchFromDt} and #{searchToDt}
	       	        	


	
	  
		and b.id like CONCAT('%',#{searchString},'%')	  
	
	
		and b.name like CONCAT('%',#{searchString},'%')	
	
	
		and ( b.phone like CONCAT('%',#{searchString},'%')	)

SELECT date_format(a.pay_dt, '%Y.%m.%d %H:%i') as pay_dt, case when a.pay_sts = 'Y' then '결제 완료' when a.pay_sts = 'C' then '결제 취소' when a.pay_sts = 'N' then '미승인' else '' end pay_sts_name, a.pay_sts, c.subject as product_name, format(a.pay_total, 0) as pay_total_str, b.id, b.name, b.phone FROM herit_pay_info a, herit_member b, herit_payplan_info c WHERE a.pay_type = #{pay_type} and a.mem_seq = b.seq and a.p_key = c.seq
and c.seq = #{seq}
and a.mem_seq = #{mem_seq}
and date_format(a.pay_dt, '%Y.%m.%d') = #{searchDt}
and date_format(a.pay_dt, '%Y.%m.%d') between #{searchFromDt} and #{searchToDt}
and a.pay_sts = #{pay_sts} and date_format(a.pay_dt, '%Y.%m.%d') between #{searchFromDt} and #{searchToDt}
and b.id like CONCAT('%',#{searchString},'%')
and b.name like CONCAT('%',#{searchString},'%')
and ( b.phone like CONCAT('%',#{searchString},'%') )
order by a.pay_dt desc LIMIT #{perPageCnt} OFFSET #{pageStartNo}

SELECT date_format(a.pay_dt, '%Y.%m.%d %H:%i') as pay_dt, case when a.pay_sts = 'Y' then '결제 완료' when a.pay_sts = 'C' then '결제 취소' when a.pay_sts = 'N' then '미승인' else '' end pay_sts_name, a.pay_sts, c.subject as product_name, format(a.pay_total, 0) as pay_total_str, b.id, b.name, b.phone, a.seq FROM herit_pay_info a, herit_member b, herit_payplan_info c WHERE a.pay_type = #{pay_type} and b.id = #{id} and a.mem_seq = b.seq and a.p_key = c.seq
and date_format(a.pay_dt, '%Y.%m.%d') = #{searchDt}
order by a.pay_dt desc
